import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import '../components/Login.css';
import logo from '../assets/logotipo.png';

function Login() {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://127.0.0.1:3333/login", {  // Envie uma solicitação para o endpoint de login no seu backend
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ email, password }),
            });

            if (response.ok) {
                // Login bem-sucedido, você pode redirecionar o usuário para a página de dashboard ou outra página apropriada
                navigate("/home");
            } else {
                const invalidUser = 'Credenciais Inválidas, por favor tente novamente.'
            }
        } catch (error) {
            // Trate erros de conexão com o servidor
            console.error(error);
        }
    };

    return (
        <div className="page">
            <form className="formLogin" onSubmit={handleLogin}>
                <img src={logo} className={"logotipo"} width={"230px"} alt="Logo" />
                <h1>Entrar</h1>
                <p>Digite os seus dados de acesso no campo abaixo.</p>
                <label htmlFor="email">E-mail</label>
                <input
                    type="email"
                    placeholder="Digite seu e-mail"
                    autoFocus={true}
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <label htmlFor="password">Senha</label>
                <input
                    type="password"
                    placeholder="Digite sua senha"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <p>Entrar em contato com o administrador</p>
                <input type="submit" value="Acessar" className="btn" />
            </form>
        </div>
    );
}

export default Login;
